import React from "react";
import "./Task.css";

 const Task = ( {task, handleTaskClick} ) => {
     return(
         <div
          className="task-container"
          style={task.completed ? {borderLeft: "6px  solid   rgba(231, 38, 183, 0.842)"} : {}}>

              <div className = "task-title" onClick={() => handleTaskClick (task.id)}> 
            {task.title} </div>
         
         <div className="buttons-container"><button className="buttons-container">X</button></div>
         
         </div>
     );
    //  return  <div className="task-container">{task.title}</div>;

 };
 export default Task;
