import React from "react";
import "./Task.css";

 const Task = ({task, handdleAddTaskClick}) => {
     return(
         <div className="task-container"
          style={task.completed ? {borderLeft: "6px  solid   aquamarine"} : {}}>
            <div className="task-title" onClick={()=>handdleAddTaskClick(task.id)}> </div>
            {task.title}
         </div>
     )
    //  return  <div className="task-container">{task.title}</div>;

 };
 export default Task;
